mapper.select:
SELECT 
	count(*)
FROM dd_driver_evaluate_info 


		and id=#{id}


		and order_no=#{orderNo}


		and car_no=#{carNo}


		and link_tel=#{linkTel}


		and create_time=#{createTime}


		and prov_code=#{provCode}


		and city_code=#{cityCode}


		and area_code=#{areaCode}


		and visit_fail_count=#{visitFailCount}


		and visit_time=#{visitTime}


		and ev_point=#{evPoint}


		and ev_time=#{evTime}

		SELECT 
			id as id,
			order_no as orderNo,
			car_no as carNo,
			link_tel as linkTel,
			create_time as createTime,
			prov_code as provCode,
			city_code as cityCode,
			area_code as areaCode,
			visit_fail_count as visitFailCount,
			visit_time as visitTime,
			ev_point as evPoint,
			ev_time as evTime
		FROM dd_driver_evaluate_info 


                  and id=#{id}


                  and order_no=#{orderNo}


                  and car_no=#{carNo}


                  and link_tel=#{linkTel}


                  and create_time=#{createTime}


                  and prov_code=#{provCode}


                  and city_code=#{cityCode}


                  and area_code=#{areaCode}


                  and visit_fail_count=#{visitFailCount}


                  and visit_time=#{visitTime}


                  and ev_point=#{evPoint}


                  and ev_time=#{evTime}
