mapper.select:
		SELECT 
			id as id,
			user_id as userId,
			trade_id as tradeId,
			trade_seq as tradeSeq,
			balance_type as balanceType,
			trade_type as tradeType,
			trade_stat as tradeStat,
			trade_time as tradeTime,
			trade_money as tradeMoney,
			total_money as totalMoney,
			trade_desc as tradeDesc,
			oper_id as operId,
			oper_time as operTime,
			ext1 as ext1,
			ext2 as ext2,
			ext3 as ext3,
			note as note
		FROM ac_acount_log 


                  and id=#{id}


                  and user_id=#{userId}


                  and trade_id=#{tradeId}


                  and trade_seq=#{tradeSeq}


                  and balance_type=#{balanceType}


                  and trade_type=#{tradeType}


                  and trade_stat=#{tradeStat}


                  and trade_time=#{tradeTime}


                  and trade_money=#{tradeMoney}


                  and total_money=#{totalMoney}


                  and trade_desc=#{tradeDesc}


                  and oper_id=#{operId}


                  and oper_time=#{operTime}


                  and ext1=#{ext1}


                  and ext2=#{ext2}


                  and ext3=#{ext3}


                  and note=#{note}


		  		ORDER BY id DESC
