mapper.select:
		select 
				t.id AS id,
				apply_id_a AS applyIdA,
				middle_id_b AS middleIdB,
				grant_id_c AS grantIdC,
				service_id AS serviceId,
				grant_type AS grantType,
				apply_date AS applyDate,
				grant_date AS grantDate,
				cancel_date AS cancelDate,
				status AS status,
				level AS level,
				c2a_fee AS c2aFee,
				b2a_fee AS b2aFee,
				c2b_fee AS c2bFee
		from ct_third_apply_info t


                  and t.id=#{id}


                  and t.apply_id_a=#{applyIdA}


                  and t.middle_id_b=#{middleIdB}


                  and t.grant_id_c=#{grantIdC}


                  and t.service_id=#{serviceId}


                  and t.grant_type=#{grantType}


                  and t.level=#{level}


                  and t.status=#{status}
