mapper.select:
select created_by as createdBy, created_date as createdDate, updated_by as updatedBy, updated_date as updatedDate, id as id, object_name as objectName, object_value as objectValue, report_no as reportNo, query_node as queryNode from fh_insure_data_info where 1=1
and id = #{id}
and report_no = #{reportNo}
and object_name = #{objectName}
and query_node = #{queryNode}
