mapper.select:
SELECT 
	count(*)
FROM fm_withdraw_order 


		and id=#{id}


		and order_id=#{orderId}


		and order_no=#{orderNo}


		and withdraw_type=#{withdrawType}


		and withdraw_reason=#{withdrawReason}


		and user_id=#{userId}


		and withdraw_time=#{withdrawTime}


		and ext1=#{ext1}


		and ext2=#{ext2}


		and ext3=#{ext3}


		and ext4=#{ext4}


		and ext5=#{ext5}

		SELECT 
			id as id,
			order_id as orderId,
			order_no as orderNo,
			withdraw_type as withdrawType,
			withdraw_reason as withdrawReason,
			user_id as userId,
			withdraw_time as withdrawTime,
			ext1 as ext1,
			ext2 as ext2,
			ext3 as ext3,
			ext4 as ext4,
			ext5 as ext5
		FROM fm_withdraw_order 


                  and id=#{id}


                  and order_id=#{orderId}


                  and order_no=#{orderNo}


				and withdraw_type=#{withdrawType}


                  and withdraw_reason=#{withdrawReason}


                  and user_id=#{userId}


                  and withdraw_time=#{withdrawTime}


                  and ext1=#{ext1}


                  and ext2=#{ext2}


                  and ext3=#{ext3}


                  and ext4=#{ext4}


                  and ext5=#{ext5}
